	fetch concrete_fish into basket;#把读到的值放入临时变量basket中
	if basket != old_word then
		if reply = "" then
			set reply = basket;
		else
			set reply = concat(reply,"、",basket);
		end if;
		set old_word = basket;#现在找到的新词，对下一次循环来说，就是旧词了
	end if;
end while;#结束循环
close concrete_fish;#关闭游标

-- 如果回答大于100个字符，只要前100个字符
