-- 割掉时间
if year_cn != "" then
	set say_cuttime = replace(say,year_cn,"");#割掉年
end if;
if month_cn != "" then
	set say_cuttime = replace(say,month_cn,"");#割掉月
end if;
if day_cn != "" then
	set say_cuttime = replace(say,day_cn,"");#割掉日
end if;
