	set say_cuttime = replace(say,hour_cn,"");#割掉小时
end if;
if minute_cn != "" then
	set say_cuttime = replace(say,minute_cn,"");#割掉分钟
end if;
if time_cn != "" then
	set say_cuttime = replace(say,time_cn,"");#割掉其它时间
end if;

END
