	if change_dur != now_hour then
		if change_dur = -1 then#-1表示未设定持续时间，首次设定
			update setting set value_col = now_hour where condition_col = "持续";
			set change_need = 1;
    end if;
	else#改变背景图片的时间正是当前的小时，没超时
		set change_need = 1;
	end if;
